```javascript
// src/validation/merchant.validation.js
const Joi = require('joi');
const { uuid } = require('./custom.validation');

const createMerchant = {
    body: Joi.object().keys({
        name: Joi.string().required(),
        email: Joi.string().required().email(),
        businessCategory: Joi.string().required(),
        // apiKey is generated by the system, not provided by user
    }),
};

const getMerchants = {
    query: Joi.object().keys({
        name: Joi.string(),
        email: Joi.string().email(),
        businessCategory: Joi.string(),
        sortBy: Joi.string(),
        limit: Joi.number().integer(),
        page: Joi.number().integer(),
    }),
};

const getMerchant = {
    params: Joi.object().keys({
        merchantId: Joi.string().custom(uuid).required(),
    }),
};

const updateMerchant = {
    params: Joi.object().keys({
        merchantId: Joi.string().custom(uuid).required(),
    }),
    body: Joi.object()
        .keys({
            name: Joi.string(),
            email: Joi.string().email(),
            businessCategory: Joi.string(),
            isActive: Joi.boolean(),
        })
        .min(1),
};

const deleteMerchant = {
    params: Joi.object().keys({
        merchantId: Joi.string().custom(uuid).required(),
    }),
};

module.exports = {
    createMerchant,
    getMerchants,
    getMerchant,
    updateMerchant,
    deleteMerchant,
};
```